job.progress
    .publisher(<span class="syntax-keyword">for</span>: \.fractionCompleted)
    .sink { completed <span class="syntax-keyword">in</span>

        <span class="syntax-number">_</span> = completed

        <span class="syntax-keyword">guard</span> <span class="syntax-keyword">let</span> progress = <span class="syntax-type">MLProgress</span>(progress: job.progress) <span class="syntax-keyword">else</span> { <span class="syntax-keyword">return</span> }

        <span class="syntax-keyword">if</span> <span class="syntax-keyword">let</span> styleLoss = progress.metrics[.styleLoss] { <span class="syntax-number">_</span> = styleLoss }

        <span class="syntax-keyword">if</span> <span class="syntax-keyword">let</span> contentLoss = progress.metrics[.contentLoss] { <span class="syntax-number">_</span> = contentLoss }

    }
    .store(<span class="syntax-keyword">in</span>: &amp;subscriptions)

