commandBuffer.addCompletedHandler { (commandBuffer) <span class="syntax-keyword">in</span>
    <span class="syntax-keyword">for</span> log <span class="syntax-keyword">in</span> commandBuffer.logs {
        <span class="syntax-keyword">let</span> encoderLabel = log.encoderLabel ?? <span class="syntax-string">"Unknown Label"</span>
        <span class="syntax-built_in">print</span>(<span class="syntax-string">"Faulting encoder \"\(encoderLabel)\""</span>)
        <span class="syntax-keyword">guard</span> <span class="syntax-keyword">let</span> debugLocation = log.debugLocation,
              <span class="syntax-keyword">let</span> functionName = debugLocation.functionName
        <span class="syntax-keyword">else</span> {
            <span class="syntax-keyword">return</span>
        }
        <span class="syntax-built_in">print</span>(<span class="syntax-string">"Faulting function \(functionName):\(debugLocation.line):\(debugLocation.column)"</span>)
    }
}

